    //       languageCode: 'es', // Cambia a tu código de idioma si es necesario
    //     },
    //   },
    // };

    try {
      // const [response] = await client.detectIntent(request);

      // const chatbotMessage = response.queryResult.fulfillmentText;

      // setMessages([
      //   ...messages,
      //   { text: userMessage, isUser: true },
      //   { text: chatbotMessage, isUser: false },
      // ]);

      setUserMessage('');
    } catch (error) {
      console.error('Error al enviar mensaje a Dialogflow:', error);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-messages">
        {messages.map((message, index) => (
          <div
            key={index}
            className={message.isUser ? 'user-message' : 'bot-message'}
          >
            {message.text}
          </div>
        ))}
      </div>
      <div className="user-input">
        <input
          type="text"
          placeholder="Escribe tu mensaje..."
          value={userMessage}
          onChange={handleUserMessageChange}
        />
        <button onClick={handleSendMessage}>Enviar</button>
      </div>
    </div>
  );
}

export default Chatbot;
